set.seed(123)
tibble(
  file = exptime$file,
  id = stringi::stri_rand_strings(length(exptime$file), 10)) -&gt; IDS


MR_data |&gt; 
  select(-id) |&gt; 
  full_join(IDS, by = "file") |&gt; 
  select(-file,
         -base_pic,
         -rotated_pic,
         ) -&gt; MR_data_prep

ST_data |&gt; 
  select(-id) |&gt; 
  full_join(IDS, by = "file") |&gt; 
  select(-file) -&gt; ST_data_prep

MS_data |&gt; 
  select(-id) |&gt; 
  full_join(IDS, by = "file") |&gt; 
  select(-file) -&gt; MS_data_prep

NASATLX_data |&gt; 
  select(-id) |&gt; 
  full_join(IDS, by = "file") |&gt;
  select(-file) |&gt; 
  pivot_wider(names_from = scale, values_from = score) |&gt; 
  mutate(PE = 20 - PE) |&gt; # reverse PE scale
  pivot_longer(cols = -c("task", "level", "id"),
               names_to = "scale", values_to = "score")  |&gt;  
  mutate(score = (score * 5) |&gt; round()) -&gt; NASATLX_data_prep

SEQUENCE_data |&gt; 
  select(-id) |&gt; 
  full_join(IDS, by = "file") |&gt; 
  select(-file) -&gt; SEQUENCE_data_prep

exptime |&gt; 
  full_join(IDS, by = "file") |&gt; 
  select(-file) -&gt; exptime_prep

MR_data_prep |&gt; 
  write_csv("../preproc-data/MR_pav_data.csv")
ST_data_prep |&gt; 
  write_csv("../preproc-data/ST_pav_data.csv")
MS_data_prep |&gt; 
  write_csv("../preproc-data/MS_pav_data.csv")
NASATLX_data_prep |&gt; 
  write_csv("../preproc-data/NASATLX_pav_data.csv")
SEQUENCE_data_prep |&gt; 
  write_csv("../preproc-data/SEQ_pav_data.csv")
exptime |&gt; 
  write_csv("../preproc-data/EXPTIME_pav_data.csv")
IDS |&gt; 
  write_csv("../preproc-data/IDS_pav_data.csv")

MR_data_prep |&gt; 
  write_csv("../preproc-data/MR_tol_data.csv")
MS_data_prep |&gt; 
  write_csv("../preproc-data/MS_tol_data.csv")
ST_data_prep |&gt; 
  write_csv("../preproc-data/ST_tol_data.csv")
NASATLX_data_prep |&gt; 
  write_csv("../preproc-data/NASATLX_tol_data.csv")
SEQUENCE_data_prep |&gt; 
  write_csv("../preproc-data/SEQ_tol_data.csv")
WEIGHTS_data_prep |&gt; 
  write_csv("../preproc-data/WEIGHTS_tol_data.csv")
